function messageInputInit(object){
  var id = object.target_input_id,
      post_path = object.post_path,
      post_data = object.post_data,
      local_storage_id = object.local_storage_id,
      return_partial = object.return_partial,
      success_callback = object.success_callback;

  var message_input_wrapper = $("[data-id='" + id + "']"),
      textarea = $("[data-id='" + id + "'] textarea"),
      hints = $("[data-id='" + id + "'] .message_hints"),
      uploader = "#" + id + "_message_input_s3-uploader",
      attachment_button = "[data-id='" + id + "'] .attachment";

  // Auto resize while typing
  autosize(textarea);

  // Display formatting hint after user input
  $(textarea).keyup(function(){
    $(hints).stop(); // interrupt all fade effects
    if($(this).val().length > 0){
      $(hints).fadeTo(300, 1);
    }else{
      $(hints).fadeTo(300, 0);
    }
  });

  // DELIVERABLE UPLOAD ----------
  // Initialize S3 uploader
  $(uploader).S3Uploader({
    additional_data: {},
    progress_bar_target: $('.js-progress-bars2')
  });
  // jQuery File Upload DropZone: textarea (as opposed to whole document)
  $(uploader).fileupload({
    dropZone: textarea
  });
  // Click attachment button
  $(attachment_button).on("click", function(){
    if($(this).hasClass("ready")){
      $(".file-upload-" + id).click(); // open upload dialog
    }
  });
  // On upload start
  $(uploader).bind('s3_uploads_start', function(e) {
    // Disable textarea
    $(textarea).attr('disabled', 'disabled');

    // Disable upload button
    $(attachment_button).removeClass("ready");
    $(attachment_button).find("i").removeClass("fa-paperclip");
    $(attachment_button).find("i").addClass("fa-spin fa-spinner");
  });
  // On upload complete
  $(uploader).bind("s3_upload_complete", function(e, content){
    // Enable upload button
    $(attachment_button).addClass("ready");
    $(attachment_button).find("i").removeClass("fa-spin fa-spinner");
    $(attachment_button).find("i").addClass("fa-paperclip");

    // Append upload to message box
    var file_icon = /image/.test(content.filetype) ? "fa-file-image-o" : "fa-file-o";
    $(message_input_wrapper).find('.deliverables').attr('data-local-storage-id', local_storage_id);
    $(message_input_wrapper).find('.deliverables').append(''+
      '<div class="deliverable">' +
        '<i class="fa ' + file_icon + '"></i>' +
        '<a class="filename" href="' + content.url + '" target="_blank">' + content.filename + '</a>' +
        '<a class="delete-deliverable" data-remote="true">' +
          '<i class="fa fa-minus"></i>' +
        '</a>' +
      '</div>' +
    '');

    autosize.update(textarea);
    $(textarea).focus();
  });
  // On upload failed
  $(uploader).bind("s3_upload_failed", function(e, content){
    // Enable upload button
    $(attachment_button).addClass("ready");
    $(attachment_button).find("i").removeClass("fa-spin fa-spinner");
    $(attachment_button).find("i").addClass("fa-paperclip");
    console.log("#{content.filename} failed to upload : #{content.error_thrown}");
  });
  // END DELIVERABLE UPLOAD ----------

  // Listen to deliverable deletions
  $(".deliverables").on("click", ".delete-deliverable", function(){
    $(this).parents('.deliverable').remove();
    if ($(message_input_wrapper).find('.deliverable').length > 0) {
      localStorage.setItem(local_storage_id + '/deliverables', $(message_input_wrapper).find('.deliverables').html());
    } else {
      localStorage.removeItem(local_storage_id + '/deliverables');
    }
  });

  // AJAX Post
  function sendMessage(){
    // add body and user_id before posting:
    post_data["body"] = $(textarea).val();
    post_data["user_id"] = gon.current_user.id;
    deliverable_ids = [];

    // Find deliverables and add them to array
    $.each($('[data-deliverable-id]'), function(i, v){
      deliverable_ids.push($(v).data('deliverable-id'));
    });

    // Remove deliverables
    $(message_input_wrapper).find('.deliverable').remove();

    $.ajax({
      url: post_path,
      type: "POST",
      data: {
        message: post_data,
        partial: return_partial,
        deliverable_ids: deliverable_ids
      },
      success: function(data){
        success_callback(data);
      },
      error: function(data){console.log(data.error);}
    })
  }

  // Submit on Enter press & Line break on Shift+Enter
  $(textarea).keypress(function(event){
    if(event.keyCode == 13 && !event.shiftKey && $(this).val().trim().length > 0){
      sendMessage();
      $(this).val("");
      autosize.update(textarea);
      // delete ALL open messages from this page when form is submitted
      localStorage.removeItem(local_storage_id);
      localStorage.removeItem(local_storage_id + '/deliverables');
      return false;
    }
  });

  // Localstorage for Message Input
  // save user's message while typing
  function saveText(){
    var text = $(textarea).val();
    // Save typed message to localStorage
    if(text !== ""){
      localStorage.setItem(local_storage_id, text);
    }else{
      localStorage.removeItem(local_storage_id);
    };
  };

  $(textarea).on("keyup", function(){
    saveText();
  });

  var open_message = localStorage.getItem(local_storage_id);
  var open_deliverables = localStorage.getItem(local_storage_id + '/deliverables');
  if(open_message) {
    $(textarea).val(open_message);
  };
  if(open_deliverables) {
    $(message_input_wrapper).find('.deliverables').html(open_deliverables);
  };
}
