function loadAjaxPanel(path,id) {
	$(id)
		.append('<img src="https://s3.amazonaws.com/coach-courses-us/public/images/ajax-loader-md.gif" alt="Loading..." title="Loading..." class="feature-ajax-spinner" />');

	$.ajax({
    url: path,
    cache: true,
    success: function(html){
      $(id)
      	.html("")
      	.hide()
      	.append(html)
      	.fadeIn();
    },
		error: function(request, textStatus, errorThrown){
			var genericError = "Sorry, an error occurred making this request. Please try again momentarily.";
			var message = (request.status == 403) ? request.responseText : genericError;
			// If opening notes panel, display error there. otherwise, load generic error modal
			if ($('#questions-contents').length > 0) {
				$('#questions-contents').html(
					'<p class="tiny_text" style="color:#888;"><span class="glyphicon glyphicon-warning-sign"></span>&nbsp;&nbsp;Error: Note content not found.</p>'
				);
			} else {
				$('.error-message-text').append(message);
				$('#error-modal').modal('show');
			}
		}
	});
}

