var rubricsFeedback = (function() {

 var keyLocalStorage = 'submission_tutor_feedback';

  var init = function() {
    bindFunctions();
    showCurrentStep();
  };

  var initializeContentForTextArea = function(){
    $('.counter_comment').html(showCharactersLeft(localStorage.getItem(keyLocalStorage)));
    $("#modal_question_editbox_text").attr('placeholder','Add a link or a tip for improvement (100 character requirement)');
  }

  // bind functions for DOM events
  var bindFunctions = function() {
    $(document).on('click', '.evaluateBackIcon--js', clickBackToCritera);
    $(document).on('click', '.evaluateCircle--js', getUrlOfLink);
    $(document).on('click', '.previewSubmit--js', clickSubmitPreview);
    $(document).on('click', '.evaluatePreviewLink--js', showPreviewTemplate);
    $(document).on('keyup', 'textarea', textBoxValidation);
    $(document).on('click', '[data-toggle="notice"]', CareerFoundyCoach.showWarningNotice);
  };

  // submits the form for comment to the submit_review action
  var clickSubmitPreview = function(e){
    e.preventDefault();
    $('#submit_review_form--js').submit();
  }

  // defines criteria when pressing the back button
  // hides divs / defines step numbers / resets svg plant images
  var clickBackToCritera = function(e) {
    e.preventDefault();

    var current_step = $(".step_description").data('step-value');
    if(current_step == 3)
    {
      $("#preview").hide();
      $('#feedback_comment').fadeIn('slow');
      $('#tips').fadeIn('slow');
      resetSvgs();

    }else if(current_step == 2)
    {
      $('#feedback').show().fadeOut('slow', function(){
        $(this).empty();
      })
      $('#evaluate_criteria').fadeIn('slow');
    }
    setCurrentStep(current_step - 1);

  };

  // responsible for filling EVALUATION STEP in the rubrics sub history_header
  // also modifies data variables
  var showCurrentStep = function(){
    var stepValue = $('.step_description').data('step-value');
    $('.bar').map(function () {
      if($(this).data().step <= stepValue) {
        $(this).addClass('filled');
      } else {
        $(this).removeClass('filled');
      }
    });
    $('.stepValue--js').html(stepValue);
  };

  // sets the current step of the evaluation process via a data value
  var setCurrentStep = function (step_number) {
    $('.step_description').data('step-value',step_number);
    showCurrentStep();
  };

  // resets the svg plant images in the preview card
  var resetSvgs = function() {
    $('.svg').each(function() {
      $(this).removeClass('fill')
    });
  };

  // adds the selected tip link to the body of the comment form
  var getUrlOfLink = function(e){
    e.preventDefault();
    var linkHref = $(this).parents('a').attr('href');
    var linkTitle = $(this).siblings('span').html();
    var toInsert = "\n"+"[" + linkTitle + "](" + linkHref + ")";
    var textValue = $('.evaluate_body .comment-form textarea').val();
    var textBox = $('.evaluate_body .comment-form textarea');
    textBox.val(textValue + '\n' + toInsert);
  };

  // shows the preview card and hides the form and feedback divs
  var showPreviewTemplate = function(e) {
    e.preventDefault();
    if(!$('.evaluatePreviewLink--js').hasClass('disabled')) {
      $("#preview").fadeIn('slow');
      $('#feedback_comment').hide();
      $('#tips').hide();
      var submissionStatusElement = $('.submission_status');
      $('.comment_text p').html(markdownConverter.makeHtml($('#modal_question_editbox_text')));
      $('.preview_criteria').html(submissionStatusElement.data('submision-status_verbose'));
      preview.fillSvg(submissionStatusElement.data('submision-status'));
      setCurrentStep(3);
    }
  };

  var showCharactersLeft  = function(value) {

    var character_count = $(returnHtmlFromString(value)).text().length,
        reverse_count = 100 - character_count;
    return (reverse_count > 0) ? ("Write " + reverse_count + " more characters.") : "";
  };

  // validates the length of the comment left by a mentor
  var textBoxValidation = function() {
    var value = $(this).val();
    if(localStorage)
    {
      localStorage.setItem(keyLocalStorage, value);
      $('.counter_comment').html(showCharactersLeft(value));
      validateLength($(returnHtmlFromString(localStorage.getItem(keyLocalStorage))).text(), 100);
    }else{
        validateLength(value, 100);
    }
  }

  var returnHtmlFromString = function(string_content){
    var converter = CareerFoundyCoach.htmlConverter();
        text = string_content
    return converter.makeHtml(text);
  }



  // function to check length within text box
  var validateLength = function(input_value, length) {
    if (input_value.length < length) {
      $('.evaluatePreviewLink--js').addClass('disabled');
    } else {
      $('.evaluatePreviewLink--js').removeClass('disabled')
    }
  }

  return {
    init: init,
    setCurrentStep: setCurrentStep,
    initializeContentForTextArea: initializeContentForTextArea
  };
})();
