  // remove text or revert to saved text on mouseout
  var removeRatingValueQuestion = function(){
    var $a = $(this)
    var $currentRating = $a.parents().siblings('p.br-current-rating')

    if($a.parents().siblings('select').val() <= 0) {
      $currentRating.html("");
    } else if ($currentRating.data("saved").length > 0) {
      $currentRating.html($currentRating.data("saved"));
    }
  }

  var setValue = function() {
    var $a = $(this)
    var $currentRating = $a.parents().siblings('p.br-current-rating')

    $currentRating.data("saved", $currentRating.html());
    $currentRating.html($currentRating.html());
  }




  //Submit the Ratings
  var submitRating = function(){
    //validations shows a warning if student hasn't rated a question
    var survey_ids = gon.survey_ids
    var ratingsArray = [];
    for (var id of survey_ids) {
      if($("#rating-" + id).val() <= 0){
        $("#rating-description-" + id).addClass("warning");
        $("#rating-description-" + id).html("Oops! Missed this one!");
      }else{
        // get all values of ratings
        // get value form checkbox
        // only submit rating if ALL 3 ratings have values greater than 0
        if(($("#rating-5").val() > 0) && ($("#rating-6").val() > 0) && ($("#rating-7").val() > 0)) {

          ratingsArray.push(
            {
              rating: $("#rating-" + id).val(),
              survey_question_id: id,
            }
          );
        };
      };
    };

    if ( !$(".br-current-rating").hasClass("warning") ) {
      $.ajax({
        url: "/submissions/" + gon.submission_id + "/create_submission_review",
        type: "POST",
        data: {
          ratings: ratingsArray,
          shared_with_mentor: $("#share_review").val(),
        },
        success: function(r){
          console.log(r);
          //close rating box and show a thank you message
          $(".mentor-review-box").html("Thanks for letting us know how things are going!");
          $("#step_rating_box").delay(1800).hide(350);

        },
        error: function(r){
          console.log(r);
        }
      });
    };

  };

  var shareReview = function(element){
    if(element.checked) {
      $("#share_review").val(true);
    } else {
      $("#share_review").val(false);
    }
  };

  var closeSurveyBox = function() {
    $("#step_rating_box").hide(350);
  }



  return {
    init: init,
    shareReview: shareReview
  }
