var CareerFoundyCoach = (function() {

  var htmlConverter = function(){
    return new showdown.Converter({ extensions: ['linebreak'] });
  };

  var signUpModal = function() {
    $('.login-modal-open--js').click(function(event){
      event.preventDefault();
      $('#course_plan_signup').modal({
        backdrop: 'static',
        keyboard: false
      });
    });
  };

  var initializeBarRating = function(){
    $(".star-rating").barrating({
      theme: 'bootstrap-stars',
      onSelect: function(value, text, event) {
        if (typeof(event) !== 'undefined' && value > 0) {
          // rating was selected by a user, submit
          $(event.target).closest('.new_rating'); //.submit();
        }
      }
    });
  };

  var initializeToolTips = function(){
    $('body').tooltip({ selector: '[data-toggle="tooltip"]'});
  };

  var adminSection = function(){
    $("#quote").on("keyup", function(){
      var charCount = $("#quote").val().length;
      $("#char-count").html(charCount);
      if (charCount > 249) {
        $("#char-count").css("color", "#FF0000");
      } else {
        $("#char-count").css("color", "#3E51A3");
      }
    });
  };

  var navDropdownHover = function(){
    if ($(window).width() > 991){
      $(".dropdown_nav--js").hover(
        function() {
            $('.dropdown-menu', this).stop( true, true ).fadeIn("fast");
            $(this).toggleClass('open');
            $(".notifications_dropdown").removeClass("open");
        },
        function() {
            $('.dropdown-menu', this).stop( true, true ).fadeOut("fast");
            $(this).toggleClass('open');
        });
    }
  };

  // this adds click notices to specified elements
  // to add an element add the given class to the leading
  // if block and add a data-toggle='notice' attribute
  // as well as a data-noticetext attribute with the
  // text you'd like to be displayed in the warning div
  var showWarningNotice = function(e){
    if ($(this).is('.disabled, .visible')) {
      var text = '<p>' + $(this).data('noticetext') + '</p>';
      var warningDiv = getWarningDiv();

      $('body').append(warningDiv);

      warningDiv.html(text)
      .css({
        'top': e.pageY - 20,
        'left': e.pageX - 20
      })
      .show()
      .delay(800).fadeOut('slow');
    }
  };

  var getWarningDiv = function() {
    if ($('.warningDiv').length) {
      return $('.warningDiv');
    } else {
      return $("<div class='warningDiv'></div>");
    }
  };

  return {
    initializeToolTips: initializeToolTips,
    adminSection: adminSection,
    signUpModal: signUpModal,
    initializeBarRating: initializeBarRating,
    htmlConverter: htmlConverter,
    navDropdownHover: navDropdownHover,
    showWarningNotice: showWarningNotice,
    getWarningDiv: getWarningDiv
  };

})();

$(document).ready(function(){
  showdown.extension('linebreak', showdownExtensions.lineBreak());
  CareerFoundyCoach.initializeToolTips();
  CareerFoundyCoach.adminSection();
  CareerFoundyCoach.initializeBarRating();
  CareerFoundyCoach.navDropdownHover();
  stepRatings.init();
  enrollPage.init();
  rubricsEvaluate.init();
  rubricsShow.init();
  rubricsFeedback.init();
  videoReview.init();
  intercom.init();
  stepConversation.init();
  testimonialsIndex.init();
  stepDeliverables.init();
  coursePrep.init();
  dashboard.init();
  simpleSubmissions.init();
  mentorDashboard.init();
  coursePages.init();
  ccaStudent.init();
  communication.init();
  mentorCapacities.init();
  cohortStudents.init();
  studentRoster.init();
  mentorReviews.init();
  courseMentors.init();
  coursePrepCarousel.init();
});
